const output = [];

const keywords = [
  "apollo", "outreach", "leads", "pipeline", "crm",
  "sales enablement", "sdr tools", "prospecting", "revenue",
  "sales navigator", "seamless.ai", "lusha", "sales tools"
];

const icpTitles = [
  "head of growth", "marketing", "growth lead", "growth manager",
  "revenue leader", "sdr", "sales development representative",
  "ae", "account executive", "gtm", "go-to-market",
  "sales ops", "sales operations", "sales enablement", "ceo"
];

for (const item of items) {
  const bio = item.json.bio?.toLowerCase() || "";
  const content = item.json.content?.toLowerCase() || "";
  const username = item.json.username || "";
  const post_url = item.json.post_url || "";
  const user_profile_url = item.json.user_profile_url || "";
  const likes = item.json.likes || 0;
  const comments = item.json.comments || 0;
  const date_posted = item.json.date_posted || "";

  let matched_keyword = null;
  for (const k of keywords) {
    const regex = new RegExp(`\\b${k}\\b`, 'i');
    if (regex.test(bio) || regex.test(content)) {
      matched_keyword = k;
      break;
    }
  }

  const isICP = icpTitles.some(title => bio.includes(title));

  // ✅ Only include posts that match both keyword AND ICP
  if (matched_keyword && isICP) {
    // Optional confidence scoring logic
    let confidence = 0.6;
    if (likes > 10) confidence += 0.2;
    if (comments > 3) confidence += 0.2;
    confidence = Math.min(confidence, 1.0);
    confidence = Math.round(confidence * 100) / 100;

    output.push({
      json: {
        post_url,
        user_profile_url,
        matched_keyword,
        confidence_score: confidence,
        reason: `Matched keyword "${matched_keyword}" in post or bio and poster fits ICP (e.g. ${bio}).`,
        username,
        content,
        bio,
        date_posted
      }
    });
  }
}

return output;

